SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_reaj_contrato () AS $body$
DECLARE


nr_seq_tabela_ant_w		bigint;
nr_seq_tabela_w			bigint;
nr_seq_plano_preco_w		bigint;
qt_idade_inicial_w		bigint;
qt_idade_final_w		bigint;
vl_preco_atual_w		double precision;
tx_acrescimo_w			double precision;
vl_preco_nao_subsidiado_w	double precision;
nr_seq_plano_preco_ww		bigint;
nr_seq_reajuste_w		bigint;
nr_seq_lote_reajuste_w		bigint;
qt_lote_reajuste_w		bigint;

C01 CURSOR FOR
	SELECT	distinct (substr(b.ds_observacao,29,3))::numeric ,
		a.nr_seq_tabela
	from	pls_segurado a,
		pls_segurado_historico b
	where	a.nr_sequencia		= b.nr_seq_segurado
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	trunc(dt_historico,'dd')	= to_date('21/05/2009')
	and	b.ds_historico 		= 'Mudan√ßa de tabela'
	and	a.nr_seq_contrato	= 59;

c02 CURSOR FOR
	SELECT	nr_sequencia,
		qt_idade_inicial,
		qt_idade_final,
		vl_preco_atual,
		tx_acrescimo,
		vl_preco_nao_subsidiado
	from	pls_plano_preco
	where	nr_seq_tabela	= nr_seq_tabela_ant_w
	order by qt_idade_inicial;

C03 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_reajuste
	from	pls_reajuste_preco
	where	nr_seq_preco	= nr_seq_plano_preco_w;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_tabela_ant_w,
	nr_seq_tabela_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open c02;
	loop
	fetch c02 into
		nr_seq_plano_preco_w,
		qt_idade_inicial_w,
		qt_idade_final_w,
		vl_preco_atual_w,
		tx_acrescimo_w,
		vl_preco_nao_subsidiado_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin

		select	nr_sequencia
		into STRICT	nr_seq_plano_preco_ww
		from	pls_plano_preco
		where	nr_seq_tabela	= nr_seq_tabela_w
		and	qt_idade_inicial	= qt_idade_inicial_w;

		open C03;
		loop
		fetch C03 into
			nr_seq_reajuste_w,
			nr_seq_lote_reajuste_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin

			select	count(*)
			into STRICT	qt_lote_reajuste_w
			from	pls_reajuste_preco
			where	nr_seq_reajuste	= nr_seq_lote_reajuste_w
			and	nr_seq_preco	= nr_seq_plano_preco_ww;

			if (qt_lote_reajuste_w = 0) then
				insert into pls_reajuste_preco(nr_sequencia,
								dt_atualizacao,
								nm_usuario,
								dt_atualizacao_nrec,
								nm_usuario_nrec,
								nr_seq_reajuste,
								dt_reajuste,
								pr_reajustado,
								vl_base,
								vl_reajustado,
								nr_seq_preco,
								nr_seq_tabela,
								dt_liberacao,
								nm_usuario_lib,
								dt_periodo_inicial,
								dt_periodo_final,
								dt_autorizacao_ans,
								vl_preco_nao_subsidiado,
								vl_preco_nao_subsid_base)
								(SELECT	nextval('pls_reajuste_preco_seq'),
									clock_timestamp(),
									'Tasy',
									clock_timestamp(),
									'Tasy',
									nr_seq_reajuste,
									dt_reajuste,
									pr_reajustado,
									vl_base,
									vl_reajustado,
									nr_seq_plano_preco_ww,
									nr_seq_tabela,
									dt_liberacao,
									nm_usuario_lib,
									dt_periodo_inicial,
									dt_periodo_final,
									dt_autorizacao_ans,
									vl_preco_nao_subsidiado,
									vl_preco_nao_subsid_base
								from	pls_reajuste_preco
								where	nr_sequencia	= nr_seq_reajuste_w);
			end if;

			end;
		end loop;
		close C03;

		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_ajustar_reaj_contrato () FROM PUBLIC;

