CREATE OR REPLACE PROCEDURE pls_ajustar_rede_referencias ( nr_seq_plano_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
nr_seq_plano_w		bigint;
cd_rede_referenci_w	varchar(30);
			
C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_plano 
	where	cd_estabelecimento	= cd_estabelecimento_p 
	and	((nr_sequencia		= nr_seq_plano_p) or (nr_seq_plano_p		= 0)) 
	and	ie_tipo_operacao	<> 'A' 
	and	((coalesce(cd_rede_refer_ptu::text, '') = '' and nr_seq_plano_p = 0) or (nr_seq_plano_p <> 0));


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_plano_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	cd_rede_referenci_w	:= pls_obter_rede_ref_produto(nr_seq_plano_w,cd_estabelecimento_p,'C');
 
	update	pls_plano 
	set	cd_rede_refer_ptu	= cd_rede_referenci_w 
	where	nr_sequencia		= nr_seq_plano_w;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
