CREATE OR REPLACE PROCEDURE pls_ajustar_renovacao_cart ( nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_contrato_w	bigint;
qt_dias_w		integer;
qt_dias_parametro_w	varchar(10);
qt_reg_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		coalesce(qt_dias_valid_cart,0)
	from	pls_contrato
	where	cd_estabelecimento	= cd_estabelecimento_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_contrato_w,
	qt_dias_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_reg_w
	from	pls_carteira_renovacao
	where	nr_seq_contrato	= nr_seq_contrato_w;

	if (qt_reg_w = 0) then
		insert into pls_carteira_renovacao(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				cd_estabelecimento,qt_meses_renovacao,nr_seq_contrato)
		values (	nextval('pls_carteira_renovacao_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				cd_estabelecimento_p,qt_dias_w,nr_seq_contrato_w);
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
