SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_repasse_segurado () AS $body$
DECLARE



nr_seq_segurado_w	bigint;
ie_tipo_repasse_w	varchar(1);
ie_preco_w		varchar(2);

C01 CURSOR FOR
	SELECT	nr_seq_segurado
	from	pls_segurado_repasse
	where	coalesce(ie_tipo_repasse::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_segurado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(b.ie_preco)
	into STRICT	ie_preco_w
	from	pls_segurado	a,
		pls_plano	b
	where	a.nr_seq_plano	= b.nr_sequencia
	and	a.nr_sequencia	= nr_seq_segurado_w;

	if (ie_preco_w = 1) then
		ie_tipo_repasse_w	:= 'P';
	else
		ie_tipo_repasse_w	:= 'C';
	end if;

	update	pls_segurado_repasse
	set	ie_tipo_repasse	= ie_tipo_repasse_w
	where	nr_seq_segurado	= nr_seq_segurado_w;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
