CREATE OR REPLACE PROCEDURE pls_ajustar_resp_isencao_caren ( cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_regra_isencao_w	bigint;
ie_mae_beneficiario_w	varchar(10);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		coalesce(ie_mae_beneficiario,'N')
	from	pls_regra_carencia_isencao
	where	cd_estabelecimento	= cd_estabelecimento_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_regra_isencao_w,
	ie_mae_beneficiario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (ie_mae_beneficiario_w	= 'S') then
		update	pls_regra_carencia_isencao
		set	ie_responsavel_plano	= 'M',
			nm_usuario		= 'TASY',
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_seq_regra_isencao_w;
	else
		update	pls_regra_carencia_isencao
		set	ie_responsavel_plano	= 'N',
			nm_usuario		= 'TASY',
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_seq_regra_isencao_w;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
