SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_seq_item_analise () AS $body$
DECLARE


nr_seq_conta_proc_w		bigint;
nr_seq_conta_mat_w		bigint;
nr_seq_proc_partic_w		bigint;
nr_seq_analise_w		bigint;
nr_seq_item_resumo_w		bigint;
nr_seq_item_resumo_glosa_w	bigint;


C01 CURSOR FOR
	SELECT	nr_seq_conta_proc,
		nr_seq_conta_mat,
		nr_seq_proc_partic,
		nr_seq_analise,
		nr_sequencia
	from	pls_analise_conta_item
	where ((nr_seq_conta_proc IS NOT NULL AND nr_seq_conta_proc::text <> '')
	or	 (nr_seq_conta_mat IS NOT NULL AND nr_seq_conta_mat::text <> '')
	or	 (nr_seq_proc_partic IS NOT NULL AND nr_seq_proc_partic::text <> ''))
	and	coalesce(nr_seq_w_resumo_conta::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_conta_proc_w,
	nr_seq_conta_mat_w,
	nr_seq_proc_partic_w,
	nr_seq_analise_w,
	nr_seq_item_resumo_glosa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(nr_sequencia)
	into STRICT	nr_seq_item_resumo_w
	from	w_pls_resumo_conta
	where	nr_seq_analise = nr_seq_analise_w
	and	((nr_seq_conta_proc  = nr_seq_conta_proc_w)
	or (nr_seq_conta_mat   = nr_seq_conta_mat_w)
	or (nr_seq_partic_proc = nr_seq_proc_partic_w));

	update	pls_analise_conta_item
	set	nr_seq_w_resumo_conta	= nr_seq_item_resumo_w
	where	nr_sequencia		= nr_seq_item_resumo_glosa_w;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
