SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_tipo_log_lead ( cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_solicitacao_w		bigint;
nr_seq_tipo_logradouro_w	bigint;
cd_tipo_logradouro_w		bigint;
cd_tipo_logradouro_sus_w	varchar(10);
ds_tipo_logradouro_w		varchar(80);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_solicitacao_comercial
	where	cd_estabelecimento	=  cd_estabelecimento_p
	and	(nr_seq_tipo_logradouro IS NOT NULL AND nr_seq_tipo_logradouro::text <> '');



BEGIN

open C01;
loop
fetch C01 into
	nr_seq_solicitacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	nr_seq_tipo_logradouro
	into STRICT	nr_seq_tipo_logradouro_w
	from	pls_solicitacao_comercial
	where	nr_sequencia	= nr_seq_solicitacao_w;

	select	cd_tipo_logradouro,
		ds_tipo_logradouro
	into STRICT	cd_tipo_logradouro_w,
		ds_tipo_logradouro_w
	from	cns_tipo_logradouro
	where	nr_sequencia	= nr_seq_tipo_logradouro_w;

	begin
	select	max(cd_tipo_logradouro)
	into STRICT	cd_tipo_logradouro_sus_w
	from	sus_tipo_logradouro
	where	(cd_tipo_logradouro)::numeric 	= nr_seq_tipo_logradouro_w;
	exception
	when others then
		cd_tipo_logradouro_sus_w	:= null;
	end;

	if (coalesce(cd_tipo_logradouro_sus_w::text, '') = '') then
		begin
		select	max(cd_tipo_logradouro)
		into STRICT	cd_tipo_logradouro_sus_w
		from	sus_tipo_logradouro
		where	upper(ds_tipo_logradouro)	= upper(ds_tipo_logradouro_w);
		exception
		when others then
			cd_tipo_logradouro_sus_w	:= null;
		end;
	end if;

	if (cd_tipo_logradouro_sus_w IS NOT NULL AND cd_tipo_logradouro_sus_w::text <> '') then
		update	pls_solicitacao_comercial
		set	CD_TIPO_LOGRADOURO	= cd_tipo_logradouro_sus_w
		where	nr_sequencia		= nr_seq_solicitacao_w;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
