DECLARE


nr_seq_intercambio_w		bigint;
ie_tipo_repasse_w		varchar(10);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_intercambio
	where	coalesce(ie_tipo_repasse::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_intercambio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(ie_tipo_repasse)
	into STRICT	ie_tipo_repasse_w
	from	pls_segurado
	where	nr_seq_intercambio = nr_seq_intercambio_w;

	update	PLS_INTERCAMBIO
	set	ie_tipo_repasse	= ie_tipo_repasse_w
	where	nr_sequencia	= nr_seq_intercambio_w;

	end;
end loop;
close C01;


commit;

end;
