SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajustar_valores_mens () AS $body$
DECLARE

 
nr_seq_lote_w		bigint;

C01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	pls_lote_mensalidade a 
	where	exists (	SELECT	1 
				from	pls_mensalidade x 
				where	a.nr_sequencia	= x.nr_seq_lote 
				and	x.ie_cancelamento	= 'E' 
				and	x.vl_mensalidade	= 0);


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_lote_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL pls_atualiza_valor_mensalidade(nr_seq_lote_w,'Tasy');
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
