CREATE OR REPLACE PROCEDURE pls_ajustar_vl_glosa ( nr_seq_protocolo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_congenere_w		bigint;
qt_dia_envio_w			bigint;
nr_sequencia_w			bigint;
dt_item_w			timestamp;
ie_item_w			varchar(10);
vl_adic_procedimento_w		double precision;
nr_seq_conta_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conta
	where	nr_seq_protocolo	= nr_seq_protocolo_p;

C02 CURSOR FOR
	SELECT	nr_sequencia,
		dt_procedimento,
		'P'
	from	pls_conta_proc
	where	nr_seq_conta	= nr_seq_conta_w
	
union all

	SELECT	nr_sequencia,
		dt_atendimento,
		'M'
	from	pls_conta_mat
	where	nr_seq_conta	= nr_seq_conta_w;

BEGIN

select	nr_seq_congenere
into STRICT	nr_seq_congenere_w
from	pls_protocolo_conta
where	nr_sequencia	= nr_seq_protocolo_p;

open C01;
loop
fetch C01 into
	nr_seq_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		nr_sequencia_w,
		dt_item_w,
		ie_item_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		if (ie_item_w = 'P') then
			begin
			select	vl_adic_procedimento
			into STRICT	vl_adic_procedimento_w
			from	ptu_nota_servico
			where	nr_seq_conta_proc	= nr_sequencia_w;
			exception
			when others then
				vl_adic_procedimento_w	:= 0;
			end;

			qt_dia_envio_w := pls_obter_data_envio_operadora(dt_item_w, nr_seq_congenere_w, null, cd_estabelecimento_p, nm_usuario_p, qt_dia_envio_w);

			if (coalesce(qt_dia_envio_w,0) <> 0) then
				if (trunc(dt_item_w + qt_dia_envio_w) < trunc(clock_timestamp())) then
					update	pls_conta_proc
					set	vl_glosa	= vl_adic_procedimento_w
					where	nr_sequencia	= nr_sequencia_w;
				end if;
			end if;
		elsif (ie_item_w = 'M') then
			select	vl_adic_procedimento
			into STRICT	vl_adic_procedimento_w
			from	ptu_nota_servico
			where	nr_seq_conta_mat	= nr_sequencia_w;

			qt_dia_envio_w := pls_obter_data_envio_operadora(dt_item_w, nr_seq_congenere_w, null, cd_estabelecimento_p, nm_usuario_p, qt_dia_envio_w);
			if (coalesce(qt_dia_envio_w,0) <> 0) then
				if (trunc(dt_item_w + qt_dia_envio_w) < trunc(clock_timestamp())) then
					update	pls_conta_mat
					set	vl_glosa	= vl_adic_procedimento_w
					where	nr_sequencia	= nr_sequencia_w;
				end if;
			end if;
		end if;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
