CREATE OR REPLACE PROCEDURE pls_ajustar_vl_trib_pagamento ( nr_seq_pag_venc_trib_p pls_pag_prest_venc_trib.nr_sequencia%type, vl_imposto_novo_p pls_pag_prest_venc_trib.vl_imposto%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_log_alteracao_w		tasy_log_alteracao.nr_sequencia%type;
vl_imposto_atual_w		pls_pag_prest_venc_trib.vl_imposto%type;


BEGIN
-- busco o valor atual do campo para salvar no log
select 	max(vl_imposto)
into STRICT 	vl_imposto_atual_w
from 	pls_pag_prest_venc_trib
where 	nr_sequencia = nr_seq_pag_venc_trib_p;

-- atualiza para o novo valor
update	pls_pag_prest_venc_trib
set	vl_imposto = vl_imposto_novo_p
where 	nr_sequencia = nr_seq_pag_venc_trib_p;

-- insere o log de alteração de tabela
insert into tasy_log_alteracao(
	nr_sequencia,
	nm_tabela,
	dt_atualizacao,
	nm_usuario,
	ds_descricao
	) values (
	nextval('tasy_log_alteracao_seq'),
	'PLS_PAG_PREST_VENC_TRIB',
	clock_timestamp(),
	nm_usuario_p,
	'Alteração realizada na função OPS - Pagamento de produção médica através da ação de botão direito Ajustar valor do tributo.'
	) returning nr_sequencia into nr_seq_log_alteracao_w;

-- insere o log de alteração do campo
insert into tasy_log_alt_campo(
	nr_sequencia,
	ds_valor_old,
	ds_valor_new,
	dt_atualizacao,
	nm_atributo,
	nm_usuario,
	nr_seq_log_alteracao
	) values (
	nextval('tasy_log_alt_campo_seq'),
	to_char(vl_imposto_atual_w),
	to_char(vl_imposto_novo_p),
	clock_timestamp(),
	'VL_IMPOSTO',
	nm_usuario_p,
	nr_seq_log_alteracao_w);

-- esta rotina é chamada diretamente do delphi, por este motivo existe o commit
commit;

end;
$body$
