CREATE OR REPLACE PROCEDURE pls_ajusta_cd_usuario_plano_op () AS $body$
DECLARE


-- Campos do cursor 1
nr_seq_mov_prod_benef_w		ptu_mov_produto_benef.nr_sequencia%type;
nr_seq_segurado_w		ptu_mov_produto_benef.nr_seq_segurado%type;
nr_seq_titular_w		ptu_mov_produto_benef.nr_seq_titular%type;

-- Outros
nr_seq_segurado_cart_w		pls_segurado_carteira.nr_sequencia%type;
cd_usuario_plano_w		pls_segurado_carteira.cd_usuario_plano%type;
cd_usuario_plano_benef_w	pls_segurado_carteira.cd_usuario_plano%type;
cd_titular_benef_plano_w	pls_segurado_carteira.cd_usuario_plano%type;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_segurado,
		nr_seq_titular
	from	ptu_mov_produto_benef;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_segurado_cart_w
from	pls_segurado_carteira;

select	max(cd_usuario_plano)
into STRICT	cd_usuario_plano_w
from	pls_segurado_carteira
where	nr_sequencia = nr_seq_segurado_cart_w;

if (length(cd_usuario_plano_w) > 13) then
	open C01;
	loop
	fetch C01 into
		nr_seq_mov_prod_benef_w,
		nr_seq_segurado_w,
		nr_seq_titular_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
			select	max(cd_usuario_plano)
			into STRICT	cd_usuario_plano_benef_w
			from	pls_segurado_carteira
			where	nr_seq_segurado = nr_seq_segurado_w;

			select	max(cd_usuario_plano)
			into STRICT	cd_titular_benef_plano_w
			from	pls_segurado_carteira
			where	nr_seq_segurado = nr_seq_titular_w;

			update	ptu_mov_produto_benef
			set	cd_usuario_plano_benef 	= cd_usuario_plano_benef_w,
				cd_plano_benef_ant 	= cd_usuario_plano_ant,
				cd_titular_benef_plano 	= coalesce(cd_titular_benef_plano_w,cd_usuario_plano_benef_w),
				cd_usuario_plano 	 = NULL,
				cd_usuario_plano_ant 	 = NULL,
				cd_titular_plano 	 = NULL
			where	nr_sequencia = nr_seq_mov_prod_benef_w;
		end;
	end loop;
	close C01;
else
	update	ptu_mov_produto_benef
	set	cd_usuario_plano_benef 	= cd_usuario_plano,
		cd_plano_benef_ant 	= cd_usuario_plano_ant,
		cd_titular_benef_plano 	= cd_titular_plano,
		cd_usuario_plano 	 = NULL,
		cd_usuario_plano_ant 	 = NULL,
		cd_titular_plano 	 = NULL;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
