SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajusta_lote_ret_mov_ans ( nm_usuario_p text) AS $body$
DECLARE


nr_seq_lote_com_w	pls_monitor_tiss_lote_ret.nr_sequencia%type;
nr_seq_arquivo_w	pls_monitor_tiss_arquivo.nr_sequencia%type;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;

C01 CURSOR FOR
	SELECT	nr_sequencia nr_seq_lote_ret,
		nr_seq_lote_monitor,
		replace(nm_arquivo,'ZTE','XTE') nm_arquivo,
		dt_mes_competencia
	from	pls_monitor_tiss_lote_ret
	where	coalesce(nr_seq_lote_com::text, '') = '';

BEGIN
cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

for r_c01_w in C01 loop
	begin

	select	max(nr_sequencia)
	into STRICT	nr_seq_lote_com_w
	from	pls_monitor_tiss_lote_com
	where	nr_seq_lote_monitor	= r_c01_w.nr_seq_lote_monitor;

	-- busca o lote do arquivo
	select	max(b.nr_sequencia)
	into STRICT	nr_seq_arquivo_w
	from	pls_monitor_tiss_lote a,
		pls_monitor_tiss_arquivo b
	where	a.ie_status = 'LG'
	and	b.nr_seq_lote_monitor = a.nr_sequencia
	and	b.nm_arquivo = r_c01_w.nm_arquivo;

	if (coalesce(nr_seq_lote_com_w::text, '') = '') then

		select	nextval('pls_monitor_tiss_lote_com_seq')
		into STRICT	nr_seq_lote_com_w
		;

		insert into pls_monitor_tiss_lote_com(	nr_sequencia,nm_usuario,dt_atualizacao,nm_usuario_nrec,dt_atualizacao_nrec,
				dt_mes_competencia,nr_seq_lote_monitor, cd_estabelecimento)
		values (	nr_seq_lote_com_w,nm_usuario_p,clock_timestamp(),nm_usuario_p,clock_timestamp(),
				r_c01_w.dt_mes_competencia,r_c01_w.nr_seq_lote_monitor, cd_estabelecimento_w);

		update	pls_monitor_tiss_lote_ret
		set	nr_seq_lote_com	= nr_seq_lote_com_w,
			nr_seq_arquivo	= nr_seq_arquivo_w
		where	nr_sequencia	= r_c01_w.nr_seq_lote_ret;
	else
		update	pls_monitor_tiss_lote_ret
		set	nr_seq_lote_com	= nr_seq_lote_com_w,
			nr_seq_arquivo	= nr_seq_arquivo_w
		where	nr_sequencia	= r_c01_w.nr_seq_lote_ret;
	end if;

	CALL pls_gerencia_envio_ans_pck.atualiza_dados_ret_lote_com(nr_seq_lote_com_w,nm_usuario_p);

	end;
end loop;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_ajusta_lote_ret_mov_ans ( nm_usuario_p text) FROM PUBLIC;

