CREATE OR REPLACE PROCEDURE pls_ajusta_pf_prestador_pgto (cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) AS $body$
DECLARE


-- Cursor de prestadores com o novo cd_pessoa_fisica
C01 CURSOR(cd_pessoa_fisica_pc	pessoa_fisica.cd_pessoa_fisica%type) FOR
	SELECT	a.nr_sequencia nr_seq_prestador
	from	pls_prestador a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_pc;

-- Cursor com os registros da pls_prestador_pagto
C02 CURSOR(	nr_seq_prestador_pc	pls_prestador.nr_sequencia%type,
		cd_pessoa_fisica_pc	pessoa_fisica.cd_pessoa_fisica%type) FOR
	SELECT	b.nr_sequencia nr_seq_prest_pagto,
		(SELECT	count(1)
		 from	pls_prestador_pagto x
		 where	x.nr_seq_prestador = b.nr_seq_prestador
		 and	x.cd_pessoa_fisica = cd_pessoa_fisica_pc) qt_reg
	from	pls_prestador_pagto b
	where	b.nr_seq_prestador = nr_seq_prestador_pc;
BEGIN

for r_C01_w in C01(cd_pessoa_fisica_p) loop
	for r_C02_w in C02(r_C01_w.nr_seq_prestador, cd_pessoa_fisica_p) loop
		-- Caso não haja registros do novo cd_pessoa_fisica para esse prestador faz o update
		if (r_C02_w.qt_reg = 0) then
			update	pls_prestador_pagto
			set	cd_pessoa_fisica = cd_pessoa_fisica_p
			where	nr_sequencia = r_C02_w.nr_seq_prest_pagto;
		end if;
	end loop;
	-- O que não fez update é pq já tem um registro válido, para não gerar registros duplicados, faz a exclusão
	delete	FROM pls_prestador_pagto
	where	nr_seq_prestador = r_C01_w.nr_seq_prestador
	and	coalesce(cd_pessoa_fisica::text, '') = '';
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
