SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajuste_contrato_reaj () AS $body$
DECLARE


nr_seq_reajuste_w	bigint;
nr_seq_contrato_w		bigint;
nr_contrato_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_reajuste
	where	(nr_seq_contrato IS NOT NULL AND nr_seq_contrato::text <> '')
	and	coalesce(nr_contrato::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_reajuste_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(nr_seq_contrato)
	into STRICT	nr_seq_contrato_w
	from	pls_reajuste
	where	nr_sequencia	= nr_seq_reajuste_w;

	if (coalesce(nr_seq_contrato_w,0) <> 0) then
		select	max(nr_contrato)
		into STRICT	nr_contrato_w
		from	pls_contrato
		where	nr_sequencia	= nr_seq_contrato_w;

		update	pls_reajuste
		set	nr_contrato	= nr_contrato_w
		where	nr_sequencia	= nr_seq_reajuste_w;
	end if;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
