SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajuste_reg_monit () AS $body$
DECLARE


ie_gera_w		varchar(1);
qtde_registros_w	bigint := 0;
			
C01 CURSOR FOR
	SELECT 	nr_seq_conta,
		dt_liquidacao,
		(	SELECT count(1)
			from pls_monitor_tiss_alt x
			where	x.nr_seq_conta = z.nr_seq_conta
			and 	ie_tipo_evento = 'PC') qt_reg_pagto
	from (	select  distinct a.nr_seq_conta,
			c.dt_liquidacao
		from  	pls_conta_medica_resumo a,
			pls_pp_prestador b,
			titulo_pagar c
		where   a.ie_situacao = 'A'
		and  	b.nr_seq_lote = a.nr_seq_pp_lote
		and  	b.nr_seq_prestador = a.nr_seq_prestador_pgto
		and 	c.nr_titulo = b.nr_titulo_pagar
		and	(c.dt_liquidacao IS NOT NULL AND c.dt_liquidacao::text <> '')
		and 	c.dt_emissao > to_date('01/06/2019')
	) z;			
			
BEGIN

	for r_c01_w in C01 loop
	
		if ( r_c01_w.qt_reg_pagto = 0 ) then
			
			ie_gera_w := pls_obter_se_pgto_monit(r_c01_w.nr_seq_conta);					
			if (ie_gera_w = 'S') then
				
				qtde_registros_w := qtde_registros_w + 1;
				CALL pls_tiss_gravar_log_monitor(	'PC',
								r_c01_w.nr_seq_conta,
								null,
								null,
								null,
								null,
								null,
								null,
								null,
								null,
								null,
								wheb_usuario_pck.get_nm_usuario,
								r_c01_w.dt_liquidacao);
								
				if (qtde_registros_w mod 100 = 0 ) then
					commit;
				end if;
			end if;
		end if;
		
	end loop;
	
	
commit;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_ajuste_reg_monit () FROM PUBLIC;

