CREATE OR REPLACE PROCEDURE pls_ajuste_sca_plano ( cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_sca_w			bigint;
nr_seq_plano_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_servico_cobertura_adic;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_sca_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	nextval('pls_plano_seq')
	into STRICT	nr_seq_plano_w
	;

	insert into pls_plano(	nr_sequencia,
					cd_estabelecimento,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					ds_plano,
					cd_codigo_ant,
					ie_situacao,
					ie_franquia,
					ie_coparticipacao,
					ie_vinculo_ativo,
					ie_vinculo_inativo,
					ie_sem_vinculo,
					ie_livre_escolha,
					cd_plano,
					nr_seq_classificacao,
					ds_descricao_plano,
					ie_utilizacao_plano)
				(SELECT	nr_seq_plano_w,
					cd_estabelecimento_p,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nm_sca,
					cd_sistema_ant,
					ie_situacao,
					'N',
					'N',
					'N',
					'N',
					'N',
					'N',
					cd_interno_sca,
					nr_seq_classificacao,
					ds_sca,
					'S'
				from	pls_servico_cobertura_adic
				where	nr_sequencia	= nr_seq_sca_w);

	update	pls_plano_area
	set	nr_seq_plano	= nr_seq_plano_w
	where	nr_seq_sca	= nr_seq_sca_w;

	update	pls_cobertura
	set	nr_seq_plano	= nr_seq_plano_w
	where	nr_seq_sca	= nr_seq_sca_w;

	update	pls_carencia
	set	nr_seq_plano	= nr_seq_plano_w
	where	nr_seq_sca	= nr_seq_sca_w;

	update	pls_tabela_preco
	set	nr_seq_plano	= nr_seq_plano_w
	where	nr_seq_sca	= nr_seq_sca_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
