SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ajuste_sub_lote_sib () AS $body$
DECLARE


nr_seq_lote_sib_w	bigint;
dt_mesano_referencia_w	timestamp;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		dt_mesano_referencia
	from	pls_lote_sib
	where	ie_tipo_envio = 'R';

BEGIN

open C01;
loop
fetch C01 into
	nr_seq_lote_sib_w,
	dt_mesano_referencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pls_lote_sib
	set	nr_seq_lote_envio	= nr_seq_lote_sib_w
	where	nr_sequencia		= nr_seq_lote_sib_w
	and	dt_mesano_referencia	= dt_mesano_referencia_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
