CREATE OR REPLACE PROCEDURE pls_ajust_lib_perfil_regra_tab ( cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_registros_w		bigint;
qt_reg_w		bigint;
CD_PERFIL_w		bigint;
nr_seq_regra_item_w	bigint;

C01 CURSOR FOR
	SELECT	CD_PERFIL,
		nr_sequencia
	from	pls_cad_regra_item_perfil
	where	NR_SEQ_ITEM	= 175;



BEGIN

select	count(1)
into STRICT	qt_registros_w
from	PLS_CADASTRO_REGRA_ITEM
where	nr_sequencia	= 565;

open C01;
loop
fetch C01 into
	CD_PERFIL_w,
	nr_seq_regra_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (qt_registros_w > 0) then
		select	count(1)
		into STRICT	qt_reg_w
		from	PLS_CAD_REGRA_ITEM_PERFIL
		where	cd_perfil	= CD_PERFIL_w
		and	NR_SEQ_ITEM	= 565;

		if (qt_reg_w = 0) then
			insert into PLS_CAD_REGRA_ITEM_PERFIL(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
					NR_SEQ_ITEM,CD_PERFIL,CD_ESTABELECIMENTO,IE_CONTROLE)
			values (	nextval('pls_cad_regra_item_perfil_seq'),clock_timestamp(),'TASY',clock_timestamp(),'TASY',
					565,CD_PERFIL_w,cd_estabelecimento_p,'T');
		end if;
	end if;

	delete	FROM PLS_CAD_REGRA_ITEM_PERFIL
	where	nr_sequencia	= nr_seq_regra_item_w;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
