SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alimenta_ajute_monitor ( nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_cta_val_p pls_monitor_tiss_cta_val.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


qt_registro_w			integer;
					
C01 CURSOR(nr_seq_conta_pc	pls_conta.nr_sequencia%type) FOR
	SELECT	nr_declaracao_obito
	from	pls_diagnost_conta_obito
	where	nr_seq_conta = nr_seq_conta_pc;
	
C02 CURSOR(nr_seq_conta_pc	pls_conta.nr_sequencia%type) FOR
	SELECT	nr_decl_nasc_vivo
	from	pls_diagnostico_nasc_vivo
	where	nr_seq_conta = nr_seq_conta_pc;
					
BEGIN

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then
	
	select	count(1)
	into STRICT	qt_registro_w
	from	pls_monitor_tiss_aj_cta
	where	nr_seq_conta = nr_seq_conta_p;
	
	if (qt_registro_w = 0) then
		
		insert	into	pls_monitor_tiss_aj_cta(	dt_solicitacao, dt_autorizacao, dt_inicio_faturamento,
				dt_fim_internacao, dt_protocolo, ie_tipo_consulta,
				ie_indicacao_acidente, ie_carater_atendimento, ie_tipo_internacao,
				ie_regime_internacao, cd_doenca, ie_tipo_atendimento,
				ie_tipo_faturamento, nr_diarias_acompanhante, nr_diarias_uti,
				ie_motivo_encerramento, nr_seq_conta, ie_recem_nasc,
				dt_atualizacao_nrec, dt_atualizacao, nm_usuario,
				nm_usuario_nrec, nr_sequencia, ie_regime_atendimento,
				ie_saude_ocupacional, ie_cobertura_especial)
		(	SELECT	dt_solicitacao, dt_autorizacao, dt_inicio_faturamento,
				dt_fim_internacao, dt_protocolo, ie_tipo_consulta,
				ie_indicacao_acidente, ie_carater_atendimento, ie_tipo_internacao,
				ie_regime_internacao, (select max(cd_doenca) from pls_diagnostico_conta where nr_seq_conta = nr_seq_conta_p), ie_tipo_atendimento,
				ie_tipo_faturamento, nr_diarias_acompanhante, nr_diarias_uti,
				ie_motivo_encerramento, nr_seq_conta, ie_recem_nasc,
				clock_timestamp(), clock_timestamp(), nm_usuario_p,
				nm_usuario_p, nextval('pls_monitor_tiss_aj_cta_seq'), ie_regime_atendimento,
				ie_saude_ocupacional, ie_cobertura_especial
			from	pls_monitor_tiss_cta_val
			where	nr_sequencia = nr_seq_cta_val_p);
		commit;
	end if;
	
	select	count(1)
	into STRICT	qt_registro_w
	from	pls_monitor_tiss_aj_obito
	where	nr_seq_conta = nr_seq_conta_p;
	
	if (qt_registro_w = 0) then
		
		for r_C01_w in C01(nr_seq_conta_p) loop
			insert	into	pls_monitor_tiss_aj_obito(	dt_atualizacao, dt_atualizacao_nrec, nm_usuario,
					nm_usuario_nrec, nr_declaracao, nr_seq_conta,
					nr_sequencia)
			values (	clock_timestamp(), clock_timestamp(), nm_usuario_p,
					nm_usuario_p, r_C01_w.nr_declaracao_obito, nr_seq_conta_p,
					nextval('pls_monitor_tiss_aj_obito_seq'));
			commit;
		end loop;
	end if;
	
	select	count(1)
	into STRICT	qt_registro_w
	from	pls_monitor_tiss_aj_nasc
	where	nr_seq_conta = nr_seq_conta_p;
	
	if (qt_registro_w = 0) then
		for r_C02_w in C02(nr_seq_conta_p) loop
			insert	into	pls_monitor_tiss_aj_nasc(	dt_atualizacao, dt_atualizacao_nrec, nm_usuario,
					nm_usuario_nrec, nr_declaracao, nr_seq_conta,
					nr_sequencia)
			values (	clock_timestamp(), clock_timestamp(), nm_usuario_p,
					nm_usuario_p, r_C02_w.nr_decl_nasc_vivo, nr_seq_conta_p,
					nextval('pls_monitor_tiss_aj_obito_seq'));
			commit;
		end loop;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_alimenta_ajute_monitor ( nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_cta_val_p pls_monitor_tiss_cta_val.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;

