CREATE OR REPLACE PROCEDURE pls_alimenta_status_arq_tiss () AS $body$
DECLARE

 
tb_ie_status_w		pls_util_cta_pck.t_varchar2_table_5;
tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;

c01 CURSOR FOR 
	SELECT	CASE WHEN coalesce(dt_geracao_arquivo::text, '') = '' THEN  'N'  ELSE 'S' END  ie_status, 
		nr_sequencia 
	from	pls_monitor_tiss_arquivo;


BEGIN 
open c01;
loop 
	fetch c01 bulk collect into	tb_ie_status_w, tb_nr_sequencia_w 
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_ie_status_w.count = 0;
 
	forall i in tb_ie_status_w.first..tb_ie_status_w.last 
		update	pls_monitor_tiss_arquivo 
		set	ie_status = tb_ie_status_w(i) 
		where	nr_sequencia = tb_nr_sequencia_w(i);
end loop;
close c01;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
