CREATE OR REPLACE PROCEDURE pls_alimenta_tb_proc_regra ( nr_seq_conta_p pls_conta.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE


tb_nr_seq_conta_proc_w		pls_util_cta_pck.t_number_table;

C01 CURSOR FOR
	SELECT	p.nr_sequencia
	from	pls_conta_proc	p
	where	p.nr_seq_conta	= nr_seq_conta_p
	and	not exists (SELECT	1
				from 	pls_conta_proc_regra	r
				where	r.nr_sequencia	= p.nr_sequencia)
	and	p.ie_status	not in ('D','M');

BEGIN

open C01;
loop
fetch C01 bulk collect into
	tb_nr_seq_conta_proc_w limit 2000;
exit when tb_nr_seq_conta_proc_w.count = 0;
	forall i in tb_nr_seq_conta_proc_w.first..tb_nr_seq_conta_proc_w.last
		insert into pls_conta_proc_regra(	nr_sequencia, dt_atualizacao, nm_usuario)
					values (	tb_nr_seq_conta_proc_w(i), clock_timestamp(), nm_usuario_p);

	commit;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
