SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alimenta_usuario_prot_rec () AS $body$
DECLARE


tb_nr_sequencia_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_usuario_w		pls_util_cta_pck.t_number_table;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		(SELECT	max(b.nr_sequencia)
		 from	pls_usuario_web b
		 where	UPPER(b.nm_usuario_web) = UPPER(a.nm_usuario_nrec)) nr_seq_usuario
	from	pls_rec_glosa_protocolo a
	where	coalesce(a.nr_seq_usuario::text, '') = ''
	and	a.ie_origem_solic in ('P', 'E');


BEGIN
Open C01;
loop
	tb_nr_sequencia_w.delete;
	tb_nr_seq_usuario_w.delete;

	fetch C01 bulk collect into tb_nr_sequencia_w, tb_nr_seq_usuario_w
	limit 500;

	exit when tb_nr_sequencia_w.count = 0;

	forall i in tb_nr_sequencia_w.first .. tb_nr_sequencia_w.last
		update	pls_rec_glosa_protocolo
		set	nr_seq_usuario = tb_nr_seq_usuario_w(i)
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
