CREATE OR REPLACE PROCEDURE pls_alterar_caepf_contrato ( nr_seq_contrato_p pls_contrato.nr_sequencia%type, cd_caepf_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


cd_caepf_w	pls_contrato.cd_caepf%type;


BEGIN

select	max(cd_caepf)
into STRICT	cd_caepf_w
from	pls_contrato
where	nr_sequencia = nr_seq_contrato_p;

update	pls_contrato
set	cd_caepf	= cd_caepf_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_contrato_p;

insert into pls_contrato_historico(nr_sequencia,
	cd_estabelecimento,
	nr_seq_contrato,
	nm_usuario,
	nm_usuario_nrec,
	dt_atualizacao,
	dt_historico,
	dt_atualizacao_nrec,
	ie_tipo_historico,
	ds_historico)
values (nextval('pls_contrato_historico_seq'),
	wheb_usuario_pck.get_cd_estabelecimento,
	nr_seq_contrato_p,
	wheb_usuario_pck.get_nm_usuario,
	wheb_usuario_pck.get_nm_usuario,
	clock_timestamp(),
	clock_timestamp(),
	clock_timestamp(),
	'103', --Alteração do CAEPF
	wheb_mensagem_pck.get_texto(1060325,'VL_ANTERIOR='||cd_caepf_w||';VL_NOVO='||cd_caepf_p)); --Alterado o campo "CAEPF" de #@VL_ANTERIOR#@ para #@VL_NOVO#@
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
