CREATE OR REPLACE PROCEDURE pls_alterar_canal_venda_sca ( nr_seq_sca_p bigint, nr_seq_canal_venda_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_canal_antigo_w		bigint;
nr_seq_segurado_w		bigint;


BEGIN

if (coalesce(nr_seq_sca_p,0) > 0) then

	select	nr_seq_vendedor_canal,
		nr_seq_segurado
	into STRICT	nr_seq_canal_antigo_w,
		nr_seq_segurado_w
	from	pls_sca_vinculo
	where	nr_sequencia = nr_seq_sca_p;

	update	pls_sca_vinculo
	set	nr_seq_vendedor_canal = nr_seq_canal_venda_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_sca_p;

	CALL pls_gerar_segurado_historico(	nr_seq_segurado_w, '60', clock_timestamp(), 'Canal de vendas anterior: ' || nr_seq_canal_antigo_w || ' - ' || substr(pls_obter_nomes_contrato(nr_seq_canal_antigo_w,'VC'),1,255),
					'pls_alterar_canal_venda_sca', null, null, null,
					null, clock_timestamp(), null, null,
					null, null, nr_seq_sca_p, null,
					nm_usuario_p, 'N');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
