CREATE OR REPLACE PROCEDURE pls_alterar_cart_intercambio ( nr_seq_segurado_p bigint, cd_carteira_intercambio_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w			bigint;
cd_cartao_intercambio_w		varchar(30);


BEGIN

select	count(*)
into STRICT 	qt_registro_w
from	pls_segurado
where	cd_cartao_intercambio	= cd_carteira_intercambio_p
and	cd_estabelecimento	= cd_estabelecimento_p;

if (qt_registro_w = 0) then

	select	max(cd_cartao_intercambio)
	into STRICT	cd_cartao_intercambio_w
	from	pls_segurado
	where	nr_sequencia = nr_seq_segurado_p;

	update	pls_segurado
	set	cd_cartao_intercambio	= cd_carteira_intercambio_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_segurado_p;

	CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '33', clock_timestamp(), 'Alteração do cartão de óbito de '|| cd_cartao_intercambio_w ||' para '|| cd_carteira_intercambio_p,
					'pls_alterar_cart_intercambio', null, null, null,
					null, null, null, null,
					null, null, null, null,
					nm_usuario_p, 'S');
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(183845,'CD_CARTAO_INTERCAMBIO='||cd_carteira_intercambio_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
