CREATE OR REPLACE PROCEDURE pls_alterar_cart_interc_benef ( nr_seq_segurado_p bigint, nr_carteira_intercambio_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w			bigint;
nr_cartao_intercambio_w		varchar(30);


BEGIN

select	count(*)
into STRICT 	qt_registro_w
from	pls_segurado		b,
	pls_segurado_carteira	a
where	a.nr_seq_segurado	= b.nr_sequencia
and	a.NR_CARTAO_INTERCAMBIO	= nr_carteira_intercambio_p
and	b.cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(b.dt_rescisao::text, '') = '';

if (qt_registro_w = 0) then
	select	max(a.NR_CARTAO_INTERCAMBIO)
	into STRICT	nr_cartao_intercambio_w
	from	pls_segurado		b,
		pls_segurado_carteira	a
	where	a.nr_seq_segurado	= b.nr_sequencia;

	update	pls_segurado_carteira
	set	NR_CARTAO_INTERCAMBIO	= nr_carteira_intercambio_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_segurado		= nr_seq_segurado_p;

	CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '57', clock_timestamp(), 'Alteração do cartão de intercâmbio de '|| nr_cartao_intercambio_w ||' para '|| nr_carteira_intercambio_p,
					'pls_alterar_cart_interc_benef', null, null, null,
					null, null, null, null,
					null, null, null, null,
					nm_usuario_p, 'N');
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(184898,'NR_CARTAO_INTERCAMBIO='||nr_carteira_intercambio_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
