CREATE OR REPLACE PROCEDURE pls_alterar_cd_anterior_contr ( nr_seq_contrato_p bigint, cd_codigo_ant_novo_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_cod_anterior_ant_w	varchar(20);


BEGIN

select	cd_cod_anterior
into STRICT	cd_cod_anterior_ant_w
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_p;

update	pls_contrato
set	cd_cod_anterior	= cd_codigo_ant_novo_p,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_contrato_p;

insert into pls_contrato_historico(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
		cd_estabelecimento, nr_seq_contrato, dt_historico, ie_tipo_historico,
		ds_historico,ds_observacao)
	values (	nextval('pls_contrato_historico_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
		cd_estabelecimento_p, nr_seq_contrato_p, clock_timestamp(), '39',
		'Código anterior antigo: ' || cd_cod_anterior_ant_w || ' para código anterior: ' || cd_codigo_ant_novo_p,'pls_alterar_cd_anterior_contr');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
