CREATE OR REPLACE PROCEDURE pls_alterar_classif_benef ( nr_seq_segurado_p bigint, nr_seq_classificacao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ds_classificacao_w		varchar(255);
nr_seq_classificacao_w		pls_classificacao_benef.nr_sequencia%type;


BEGIN
select	max(nr_seq_classificacao)
into STRICT	nr_seq_classificacao_w
from 	pls_segurado
where	nr_sequencia		= nr_seq_segurado_p;

if (nr_seq_classificacao_w IS NOT NULL AND nr_seq_classificacao_w::text <> '') then
	select 	ds_classificacao
	into STRICT	ds_classificacao_w
	from 	pls_classificacao_benef
	where 	nr_sequencia 		= nr_seq_classificacao_w;
end if;

update	pls_segurado
set	nr_seq_classificacao	= nr_seq_classificacao_p,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_segurado_p;

CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '84', clock_timestamp(), 'Classificação anterior: ' || ds_classificacao_w,
				'pls_alterar_classif_benef', null, null, null,
				null, null, null, null,
				null, null, null, null,
				nm_usuario_p, 'N');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
