CREATE OR REPLACE PROCEDURE pls_alterar_classif_contrato ( nr_seq_contrato_p pls_contrato.nr_sequencia%type, cd_classif_p pls_classificacao_contrato.cd_classificacao%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_classif_ant_w	pls_contrato.nr_seq_classificacao%type;
ds_classif_ant_w	pls_classificacao_contrato.ds_classificacao%type;
ds_classif_nova_w	pls_classificacao_contrato.ds_classificacao%type;
cd_classif_w		pls_contrato.cd_classif_contrato%type;
nr_seq_classif_w	pls_classificacao_contrato.nr_sequencia%type;


BEGIN

select	max(nr_seq_classificacao)
into STRICT	nr_seq_classif_ant_w
from	pls_contrato
where	nr_sequencia = nr_seq_contrato_p;

if (nr_seq_classif_ant_w IS NOT NULL AND nr_seq_classif_ant_w::text <> '') then
	select	ds_classificacao
	into STRICT	ds_classif_ant_w
	from	pls_classificacao_contrato
	where	nr_sequencia	= nr_seq_classif_ant_w;
end if;

if (coalesce(cd_classif_p,' ') <> ' ') then
	
	select	nr_sequencia,
		ds_classificacao
	into STRICT	nr_seq_classif_w,
		ds_classif_nova_w
	from	pls_classificacao_contrato
	where	cd_classificacao	= cd_classif_p
	and	cd_estabelecimento	= cd_estabelecimento_p;
	
	update	pls_contrato
	set	cd_classif_contrato	= cd_classif_p,
		nr_seq_classificacao	= nr_seq_classif_w
	where	nr_sequencia		= nr_seq_contrato_p;

	insert into pls_contrato_historico(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			cd_estabelecimento, nr_seq_contrato, dt_historico, ie_tipo_historico, ds_historico,
			ds_observacao)
		values (	nextval('pls_contrato_historico_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
			cd_estabelecimento_p, nr_seq_contrato_p, clock_timestamp(), '83', wheb_mensagem_pck.get_texto(1140275, 'DS_CLASSIF_ANT='||coalesce(ds_classif_ant_w,' ')||';DS_CLASSIFICACAO='||ds_classif_nova_w),
			'pls_alterar_classif_contrato');
	
elsif (coalesce(cd_classif_p,' ') = ' ') then
	update	pls_contrato
	set	cd_classif_contrato	 = NULL,
		nr_seq_classificacao	 = NULL
	where	nr_sequencia		= nr_seq_contrato_p;
	
	insert into pls_contrato_historico(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			cd_estabelecimento, nr_seq_contrato, dt_historico, ie_tipo_historico, ds_historico,
			ds_observacao)
		values (	nextval('pls_contrato_historico_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
			cd_estabelecimento_p, nr_seq_contrato_p, clock_timestamp(), '83', wheb_mensagem_pck.get_texto(1140275, 'DS_CLASSIF_ANT='||coalesce(ds_classif_ant_w,' ')||';DS_CLASSIFICACAO='||null),
			'pls_alterar_classif_contrato');
end if;

commit;

end;
$body$
