CREATE OR REPLACE PROCEDURE pls_alterar_cod_item_analise ( nr_seq_item_p bigint, cd_item_p text, ds_item_p text, ie_tipo_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w		varchar(20);
nr_seq_analise_w	bigint;
nr_seq_conta_mat_w	bigint;
nr_seq_conta_proc_w	bigint;
nr_seq_item_w		bigint;


BEGIN
select	max(a.nr_seq_analise),
	max(a.nr_seq_conta_proc),
	max(a.nr_seq_conta_mat),
	max(a.nr_seq_item)
into STRICT	nr_seq_analise_w,
	nr_seq_conta_proc_w,
	nr_seq_conta_mat_w,
	nr_seq_item_w
from	w_pls_resumo_conta	a
where	a.nr_sequencia	= nr_seq_item_p;

if (nr_seq_analise_w IS NOT NULL AND nr_seq_analise_w::text <> '') then
	if (ie_tipo_p = 'P') then
		update	w_pls_resumo_conta
		set	cd_item_convertido	= cd_item_p,
			ds_item_convertido	= ds_item_p
		where	nr_sequencia		= nr_seq_item_p;

		update	pls_conta_pos_estabelecido
		set	cd_item_convertido	= cd_item_p,
			ds_item_convertido	= ds_item_p,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_seq_conta_proc	= coalesce(nr_seq_conta_proc_w,nr_seq_item_w)
		and	((ie_situacao		= 'A') or (coalesce(ie_situacao::text, '') = ''));
	else
		update	w_pls_resumo_conta
		set	cd_item_convertido	= cd_item_p,
			ds_item_convertido	= ds_item_p
		where	nr_sequencia		= nr_seq_item_p;

		update	pls_conta_pos_estabelecido
		set	cd_item_convertido	= cd_item_p,
			ds_item_convertido	= ds_item_p,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_seq_conta_mat	= coalesce(nr_seq_conta_mat_w,nr_seq_item_w)
		and	((ie_situacao		= 'A') or (coalesce(ie_situacao::text, '') = ''));
	end if;

	insert into pls_hist_analise_conta(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_analise,
		ie_tipo_historico,
		ds_observacao,
		ds_call_stack)
	values (nextval('pls_hist_analise_conta_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_analise_w,
		34,
		'CÃ³digo do item alterado por: ' || nm_usuario_p,
		substr(dbms_utility.format_call_stack,1,4000));
end if;

commit;

end;
$body$
