SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alterar_cooperativa_event ( nr_seq_segurado_p bigint, nr_seq_congenere_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_congenere_ant_w	pls_segurado.nr_seq_congenere%type;


BEGIN

if (nr_seq_congenere_p IS NOT NULL AND nr_seq_congenere_p::text <> '') then
	select	max(nr_seq_congenere)
	into STRICT	nr_seq_congenere_ant_w
	from	pls_segurado
	where	nr_sequencia		= nr_seq_segurado_p;
	nr_seq_congenere_ant_w	:= coalesce(nr_seq_congenere_ant_w,0);

	if (nr_seq_congenere_ant_w <> coalesce(nr_seq_congenere_p,0)) then
		update	pls_segurado
		set	nr_seq_congenere	= CASE WHEN nr_seq_congenere_p=0 THEN null  ELSE nr_seq_congenere_p END ,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_seq_segurado_p;

		CALL pls_gerar_segurado_historico(
			nr_seq_segurado_p, '81', clock_timestamp(),
			wheb_mensagem_pck.get_texto(357298, 'NR_SEQ_CONGENERE_P=' || to_char(nr_seq_congenere_ant_w) ) ,'pls_alterar_cooperativa_event', null,
			null, null, null,
			clock_timestamp(), null, null,
			null, null, null,
			null, nm_usuario_p, 'S');
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
