CREATE OR REPLACE PROCEDURE pls_alterar_copia_sca_plano ( nr_seq_proposta_benef_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_copiar_sca_plano_w	varchar(10);
ds_alteracao_nascido_w	varchar(100);


BEGIN

select	coalesce(ie_copiar_sca_plano,'S')
into STRICT	ie_copiar_sca_plano_w
from	pls_proposta_beneficiario
where	nr_sequencia		= nr_seq_proposta_benef_p;

update	pls_proposta_beneficiario
set	ie_copiar_sca_plano	= CASE WHEN ie_copiar_sca_plano_w='S' THEN 'N'  ELSE 'S' END ,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_proposta_benef_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
