SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alterar_dados_conta ( nr_seq_conta_p bigint, nr_seq_prestador_exec_p bigint) AS $body$
DECLARE


nr_sequencia_w			varchar(10);

BEGIN

update   pls_conta
set      nr_seq_prestador_exec 	= CASE WHEN nr_seq_prestador_exec_p='' THEN nr_seq_prestador_exec  ELSE nr_seq_prestador_exec_p END
where    nr_sequencia		= nr_seq_conta_p;

update	pls_conta_medica_resumo
set	nr_seq_prestador_exec	= CASE WHEN nr_seq_prestador_exec_p='' THEN nr_seq_prestador_exec  ELSE nr_seq_prestador_exec_p END
where	nr_seq_conta		= nr_seq_conta_p
and	ie_situacao		= 'A';

if (nr_seq_prestador_exec_p IS NOT NULL AND nr_seq_prestador_exec_p::text <> '') 	then
	select	max(a.nr_sequencia)
	into STRICT	nr_sequencia_w
	from 	tiss_motivo_glosa	b,
		pls_conta_glosa		a
	where 	b.nr_sequencia		= a.nr_seq_motivo_glosa
	and	b.cd_motivo_glosa 	= '1311'
	and 	a.nr_seq_conta 		= nr_seq_conta_p;

	if (coalesce(nr_sequencia_w,0) > 0)	then
		delete 	FROM pls_conta_glosa
		where	nr_sequencia 	= nr_sequencia_w;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
