CREATE OR REPLACE PROCEDURE pls_alterar_dados_sca_vinculo ( nr_seq_vinculo_sca_p bigint, nr_seq_pagador_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE

 
/* 
ie_opcao_p 
	P = Pagador 
*/
 
 
nr_seq_pagador_w	bigint;
nr_seq_segurado_w	bigint;
nr_seq_seg_historico_w	bigint;
ds_historico_w		varchar(255);
ds_observacao_w		varchar(255);


BEGIN 
 
select	nr_seq_segurado, 
	nr_seq_pagador 
into STRICT	nr_seq_segurado_w, 
	nr_seq_pagador_w 
from	pls_sca_vinculo 
where	nr_sequencia	= nr_seq_vinculo_sca_p;
 
if (ie_opcao_p = 'P') then 
	if (coalesce(nr_seq_pagador_w,0) <> 0) then 
		ds_historico_w	:= 'Pagador anterior: '||nr_seq_pagador_w||' - '||pls_obter_dados_pagador(nr_seq_pagador_w,'N');
	else 
		ds_historico_w	:= 'Pagador anterior: ';
	end if;
	 
	update	pls_sca_vinculo 
	set	nr_seq_pagador	= nr_seq_pagador_p 
	where	nr_sequencia	= nr_seq_vinculo_sca_p;
	 
	select	nextval('pls_segurado_historico_seq') 
	into STRICT	nr_seq_seg_historico_w 
	;
	 
	CALL pls_gerar_segurado_historico(	nr_seq_segurado_w, '6', clock_timestamp(), ds_historico_w, 
					'pls_alterar_dados_sca_vinculo', null, null, null, 
					null, clock_timestamp(), null, null, 
					null, null, nr_seq_vinculo_sca_p, null, 
					nm_usuario_p, 'N');
				 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
