CREATE OR REPLACE PROCEDURE pls_alterar_datas_conta_medica ( nr_sequencia_p bigint, dt_alteracao_p timestamp, ie_tipo_data_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_mes_competencia_w		varchar(1)	:= 'N';
ie_contabilidade_w		varchar(1)	:= 'N';
qt_lote_contabil_w		integer	:= 0;

/* IE_TIPO_DATA_P
	1 - ReferÃªncia do protocolo (DT_MES_COMPETENCIA)
*/
BEGIN
ie_mes_competencia_w	:= pls_obter_se_mes_fechado(dt_alteracao_p, 'M', cd_estabelecimento_p);
ie_contabilidade_w	:= pls_obter_se_mes_fechado(dt_alteracao_p, 'C', cd_estabelecimento_p);
if (ie_mes_competencia_w	= 'S') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 266987, null);
end if;
if (ie_contabilidade_w	= 'S') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 266988, null);
end if;

if (ie_tipo_data_p	= 1) then
	select	count(*)
	into STRICT	qt_lote_contabil_w
	from	pls_protocolo_conta
	where	coalesce(nr_lote_contabil,0) > 0
	and	nr_sequencia	= nr_sequencia_p;
	if (qt_lote_contabil_w	> 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 266990, null);
	end if;

	update	pls_protocolo_conta
	set	dt_mes_competencia	= dt_alteracao_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_sequencia_p;
end if;

commit;

end;
$body$
