CREATE OR REPLACE PROCEDURE pls_alterar_data_reinclusao ( nr_seq_reinclusao_benef_p pls_reinclusao_benef.nr_sequencia%type, dt_reinclusao_nova_p pls_reinclusao_benef.dt_reinclusao%type, nm_usuario_p text) AS $body$
DECLARE


dt_reinclusao_ant_w	pls_reinclusao_benef.dt_reinclusao%type;
dt_confirmacao_w	pls_reinclusao_benef.dt_confirmacao%type;
dt_rejeicao_w		pls_reinclusao_benef.dt_rejeicao%type;


BEGIN

select	trunc(dt_reinclusao, 'dd'),
	dt_confirmacao,
	dt_rejeicao
into STRICT	dt_reinclusao_ant_w,
	dt_confirmacao_w,
	dt_rejeicao_w
from	pls_reinclusao_benef
where	nr_sequencia = nr_seq_reinclusao_benef_p;

if	((trunc(dt_reinclusao_ant_w,'dd') = trunc(dt_reinclusao_nova_p,'dd')) or (dt_confirmacao_w IS NOT NULL AND dt_confirmacao_w::text <> '') or (dt_rejeicao_w IS NOT NULL AND dt_rejeicao_w::text <> '')) then
	null;
else
	update	pls_reinclusao_benef
	set	dt_reinclusao = dt_reinclusao_nova_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_reinclusao_benef_p;

	insert into pls_reinc_benef_historico(nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
		nm_usuario, nm_usuario_nrec, ds_historico,
		dt_liberacao, ie_tipo_historico, nr_seq_reinclusao)
	values (nextval('pls_reinc_benef_historico_seq'), clock_timestamp(), clock_timestamp(),
		nm_usuario_p, nm_usuario_p, 'Data anterior: ' || to_char(dt_reinclusao_ant_w, 'dd/mm/yyyy') || chr(13) || 'Nova data: ' || to_char(dt_reinclusao_nova_p, 'dd/mm/yyyy'),
		clock_timestamp(), 2, nr_seq_reinclusao_benef_p);

	commit;
end if;

end;
$body$
