CREATE OR REPLACE PROCEDURE pls_alterar_data_rescisao_prog ( nr_seq_rescisao_p bigint, dt_rescisao_p timestamp, ie_gerar_via_adic_p text, nr_seq_motivo_via_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
nr_seq_segurado_w		bigint;
nr_seq_pagador_w		bigint;
nr_seq_carteira_w		bigint;
ie_gerar_validade_cartao_w	varchar(1);

C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_segurado 
	where	nr_seq_pagador	= nr_seq_pagador_w 
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
	and	((coalesce(dt_rescisao::text, '') = '') or (dt_rescisao > clock_timestamp()));


BEGIN 
 
update	pls_rescisao_contrato 
set	dt_rescisao	= fim_dia(dt_rescisao_p) 
where	nr_sequencia	= nr_seq_rescisao_p;
 
if (ie_gerar_via_adic_p = 'S') then 
	select	nr_seq_segurado, 
		nr_seq_pagador 
	into STRICT	nr_seq_segurado_w, 
		nr_seq_pagador_w 
	from	pls_rescisao_contrato 
	where	nr_sequencia	= nr_seq_rescisao_p;
	 
	select	max(ie_gerar_validade_cartao) 
	into STRICT	ie_gerar_validade_cartao_w 
	from	pls_parametros 
	where	cd_estabelecimento	= cd_estabelecimento_p;
	 
	if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then 
		select	max(nr_sequencia) 
		into STRICT	nr_seq_carteira_w 
		from	pls_segurado_carteira 
		where	nr_seq_segurado	= nr_seq_segurado_w;
		 
		CALL pls_gerar_via_carteira(nr_seq_carteira_w, nr_seq_motivo_via_p, nm_usuario_p, '1202', 'M', 'N');
		 
		if (ie_gerar_validade_cartao_w = 'S') then 
			update	pls_segurado_carteira 
			set	dt_validade_carteira	= fim_dia(dt_rescisao_p) 
			where	nr_sequencia		= nr_seq_carteira_w;
		end if;
	elsif (nr_seq_pagador_w IS NOT NULL AND nr_seq_pagador_w::text <> '') then 
		open C01;
		loop 
		fetch C01 into	 
			nr_seq_segurado_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_carteira_w 
			from	pls_segurado_carteira 
			where	nr_seq_segurado	= nr_seq_segurado_w;
			 
			CALL pls_gerar_via_carteira(nr_seq_carteira_w, nr_seq_motivo_via_p, nm_usuario_p, '1202', 'M', 'N');
			 
			if (ie_gerar_validade_cartao_w = 'S') then 
				update	pls_segurado_carteira 
				set	dt_validade_carteira	= fim_dia(dt_rescisao_p) 
				where	nr_sequencia		= nr_seq_carteira_w;
			end if;
			end;
		end loop;
		close C01;
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
