SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alterar_data_validade_guia ( nr_seq_guia_p bigint, dt_nova_validade_p timestamp, ie_gerar_nova_senha_p text, nm_usuario_p text) AS $body$
DECLARE


dt_validade_senha_w		timestamp;
cd_senha_w			varchar(20);
cd_senha_velha_w		varchar(20);
ds_texto_w			varchar(1000);


BEGIN

select	dt_validade_senha,
	cd_senha
into STRICT	dt_validade_senha_w,
	cd_senha_velha_w
from	pls_guia_plano
where	nr_sequencia = nr_seq_guia_p;

if (trunc(dt_validade_senha_w) > trunc(dt_nova_validade_p)) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(178162,'');
	--'Data informada não pode ser menor que a data de validade atual!'||'#@#@');
end if;


ds_texto_w := 'O usuário '||obter_nome_usuario(nm_usuario_p) ||' alterou a data de validade da guia de '||
				dt_validade_senha_w || ' para '||dt_nova_validade_p||'.';
if (ie_gerar_nova_senha_p = 'S') then
	cd_senha_w := Obter_Numero_Randomico;
	ds_texto_w := ds_texto_w || chr(13) || 'Alterada a senha da guia de '||cd_senha_velha_w ||' para '||cd_senha_w||'.';
else
	cd_senha_w := cd_senha_velha_w;
end if;

update 	pls_guia_plano
set	dt_validade_senha = dt_nova_validade_p,
	cd_senha	= cd_senha_w,
	nm_usuario 	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia 	= nr_seq_guia_p;

CALL pls_guia_gravar_historico(nr_seq_guia_p, 2, ds_texto_w,
			null, nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
