CREATE OR REPLACE PROCEDURE pls_alterar_data_venc_fatura ( nr_seq_fatura_p bigint, dt_vencimento_p timestamp, nm_usuario_p text) AS $body$
BEGIN

if (trunc(dt_vencimento_p,'dd') < trunc(clock_timestamp(),'dd')) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(443801);
end if;

update	pls_fatura
set	DT_VENCIMENTO_MANUAL	= dt_vencimento_p,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_fatura_p;

commit;

end;
