CREATE OR REPLACE PROCEDURE pls_alterar_dias_carencias ( nr_seq_tipo_opcao_p bigint, ds_lista_nr_sequencia_p text, ie_opcao_p text, qt_dias_p bigint) AS $body$
DECLARE


ds_lista_nr_sequencia_w	varchar(2000);
nr_pos_virgula_w	bigint;
nr_sequencia_w		bigint;
nr_seq_tipo_carencia_w	bigint;
ie_opcao_w		varchar(10);

BEGIN
if (ds_lista_nr_sequencia_p IS NOT NULL AND ds_lista_nr_sequencia_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (qt_dias_p IS NOT NULL AND qt_dias_p::text <> '') then
	begin

	ds_lista_nr_sequencia_w := ds_lista_nr_sequencia_p;

	while(ds_lista_nr_sequencia_w IS NOT NULL AND ds_lista_nr_sequencia_w::text <> '') loop
		begin
		nr_pos_virgula_w	:= position(',' in ds_lista_nr_sequencia_w);

		if (nr_pos_virgula_w > 0) then
			begin
			nr_sequencia_w		:= substr(ds_lista_nr_sequencia_w,0,nr_pos_virgula_w-1);
			ds_lista_nr_sequencia_w	:= substr(ds_lista_nr_sequencia_w,nr_pos_virgula_w+1,length(ds_lista_nr_sequencia_w));
			end;
		else
			begin
			nr_sequencia_w		:= (ds_lista_nr_sequencia_w)::numeric;
			ds_lista_nr_sequencia_w	:= null;
			end;
		end if;

		if (coalesce(nr_sequencia_w,0) > 0) then
			begin

			select	coalesce(max(nr_seq_tipo_carencia),0)
			into STRICT	nr_seq_tipo_carencia_w
			from	w_pls_carencia
			where	nr_sequencia = nr_sequencia_w;

			if ('C' = ie_opcao_p) or ('S' = ie_opcao_p) or ('P' = ie_opcao_p) then
				/*aaschlote 24/02/2011 - Realizando alterações na procedure pls_alterar_dias_carencia, verificar e alterar */

				ie_opcao_w	:= 'S';

				/*pls_alterar_dias_carencia(
					nr_seq_tipo_opcao_p,
					nr_sequencia_w,
					nr_seq_tipo_carencia_w,
					qt_dias_p,
					ie_opcao_p);*/
			end if;
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
