CREATE OR REPLACE PROCEDURE pls_alterar_dt_inclus_usu_evnt ( nr_seq_segurado_p bigint, dt_inclusao_operadora_p timestamp, dt_contratacao_p timestamp, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Alterar a data de inclusão de usuários eventuais
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
dt_inclusao_operadora_w		timestamp;
ds_observacao_w			varchar(4000);


BEGIN
if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	select	dt_inclusao_operadora
	into STRICT	dt_inclusao_operadora_w
	from	pls_segurado
	where	nr_sequencia		= nr_seq_segurado_p;

	update	pls_segurado
	set	dt_inclusao_operadora	= dt_inclusao_operadora_p,
		dt_contratacao		= dt_contratacao_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_segurado_p;

	ds_observacao_w	:= 'Alterada a data de inclusão na operadora de '|| dt_inclusao_operadora_w ||' para '|| dt_inclusao_operadora_p;
end if;


/* Gerar histórico */

CALL pls_gerar_segurado_historico(
	nr_seq_segurado_p, '16', clock_timestamp(),
	'Alteração da data de inclusão', ds_observacao_w, null,
	null, null, null,
	null, null, null,
	null, null, null,
	null, nm_usuario_p, 'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
