CREATE OR REPLACE PROCEDURE pls_alterar_dt_inicio_vig_cart ( nr_seq_carteira_p bigint, dt_inicio_vigencia_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/*Utilizada nas funções OPS - Controle de Usuários Eventuais*/

dt_inicio_vigencia_w		timestamp;
nr_seq_carteira_w		bigint;
nr_seq_segurado_w		bigint;


BEGIN

select	nr_seq_segurado,
	dt_inicio_vigencia
into STRICT	nr_seq_segurado_w,
	dt_inicio_vigencia_w
from	pls_segurado_carteira
where	nr_sequencia	= nr_seq_carteira_p;

update	pls_segurado_carteira
set	dt_inicio_vigencia 	= dt_inicio_vigencia_p,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_carteira_p;

CALL pls_gerar_segurado_historico(	nr_seq_segurado_w, '72', clock_timestamp(), 'Alteração de data inicío vigência do cartão de: '||dt_inicio_vigencia_w||' para: '||dt_inicio_vigencia_p,
				'pls_alterar_dt_inicio_vig_cart', null, null, null,
				null, null, null, null,
				null, null, null, null,
				nm_usuario_p, 'N');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
