CREATE OR REPLACE PROCEDURE pls_alterar_dt_venc_pagamento ( nm_usuario_p text, nr_seq_pag_prest_venc_p bigint, dt_vencimento_p text, nr_titulo_p bigint) AS $body$
BEGIN
if (dt_vencimento_p IS NOT NULL AND dt_vencimento_p::text <> '') and (nr_seq_pag_prest_venc_p IS NOT NULL AND nr_seq_pag_prest_venc_p::text <> '') then

	update	pls_pag_prest_vencimento
	set	dt_vencimento	= dt_vencimento_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia 	= nr_seq_pag_prest_venc_p;

	if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
		update	titulo_pagar
		set	dt_vencimento_atual = dt_vencimento_p,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_titulo	= nr_titulo_p;
	end if;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
