CREATE OR REPLACE PROCEDURE pls_alterar_emissor_contrato ( nr_seq_contrato_p bigint, nr_seq_emissor_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_emissor_novo_w	varchar(255);
ds_emissor_ant_w	varchar(255);
nr_seq_emissor_ant_w	bigint;


BEGIN

select	nr_seq_emissor
into STRICT	nr_seq_emissor_ant_w
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_p;

update	pls_contrato
set	nr_seq_emissor	= nr_seq_emissor_p,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_contrato_p;

select	ds_emissor
into STRICT	ds_emissor_ant_w
from	pls_emissor_carteira
where	nr_sequencia	= nr_seq_emissor_ant_w;

select	ds_emissor
into STRICT	ds_emissor_novo_w
from	pls_emissor_carteira
where	nr_sequencia	= nr_seq_emissor_p;

insert into pls_contrato_historico(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
		cd_estabelecimento, nr_seq_contrato, dt_historico, ie_tipo_historico, ds_historico,
		ds_observacao)
	values (	nextval('pls_contrato_historico_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
		cd_estabelecimento_p, nr_seq_contrato_p, clock_timestamp(), '31', 'Emissor antigo: ' || ds_emissor_ant_w|| ' para o emissor: ' ||ds_emissor_novo_w,
		'pls_alterar_emissor_contrato');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
