CREATE OR REPLACE PROCEDURE pls_alterar_estagios_cartao ( nr_seq_cartao_seg_p bigint, dt_alteracao_p timestamp, ie_estagio_emissao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_estagio_cartao_w		bigint;
qt_validada_carteira_w		bigint;
nr_via_w			bigint;
dt_validade_cartao_w		timestamp;


BEGIN

select	count(*)
into STRICT	qt_validada_carteira_w
from	pls_segurado_carteira
where	nr_sequencia			= nr_seq_cartao_seg_p;

if (qt_validada_carteira_w > 0) then
	begin
	select	nextval('pls_segurado_cart_estagio_seq')
	into STRICT	nr_seq_estagio_cartao_w
	;

	select	nr_via_solicitacao,
		dt_validade_carteira
	into STRICT	nr_via_w,
		dt_validade_cartao_w
	from	pls_segurado_carteira
	where	nr_sequencia	= nr_seq_cartao_seg_p;

	insert	into	pls_segurado_cart_estagio(	nr_sequencia,
			nr_seq_cartao_seg,
			dt_alteracao,
			ie_estagio_emissao,
			nm_usuario_estagio,
			cd_estabelecimento,
			nm_usuario,
			nm_usuario_nrec,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nr_via,
			dt_validade_carteira			)
	values (	nr_seq_estagio_cartao_w,
			nr_seq_cartao_seg_p,
			dt_alteracao_p,
			ie_estagio_emissao_p,
			nm_usuario_p,
			cd_estabelecimento_p,
			nm_usuario_p,
			nm_usuario_p,
			clock_timestamp(),
			clock_timestamp(),
			nr_via_w,
			dt_validade_cartao_w);
	end;
end if;

--commit;
end;
$body$
