CREATE OR REPLACE PROCEDURE pls_alterar_estrut_lista_mat ( ds_lista_materiais_p text, nr_seq_estrut_mat_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Executar a procedure PLS_MATERIAL_ALTERAR_ESTRUTURA para todos os materiais selecionados.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_lista_materiais_w		varchar(4000);
nr_seq_material_w		varchar(4000);
nr_pos_virgula_w		bigint;


BEGIN
if (ds_lista_materiais_p IS NOT NULL AND ds_lista_materiais_p::text <> '') then
	ds_lista_materiais_w := ds_lista_materiais_p;

	while(ds_lista_materiais_w IS NOT NULL AND ds_lista_materiais_w::text <> '') loop
		nr_pos_virgula_w	:= position(',' in ds_lista_materiais_w);

		if (nr_pos_virgula_w > 0) then
			nr_seq_material_w 	:= substr(ds_lista_materiais_w, 1, nr_pos_virgula_w - 1);
			ds_lista_materiais_w 	:= substr(ds_lista_materiais_w, nr_pos_virgula_w + 1, length(ds_lista_materiais_w));
		else
			nr_seq_material_w	:= ds_lista_materiais_w;
			ds_lista_materiais_w	:= null;
		end if;

		if (nr_seq_material_w IS NOT NULL AND nr_seq_material_w::text <> '') then
			CALL pls_material_alterar_estrutura(nm_usuario_p, nr_seq_estrut_mat_p, nr_seq_material_w);
		end if;
	end loop;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
