CREATE OR REPLACE PROCEDURE pls_alterar_faixa_salarial ( nr_seq_segurado_p bigint, nr_seq_faixa_salarial_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_faixa_salarial_w		bigint;
ds_faixa_salarial_w		varchar(255);
ds_faixa_salarial_nova_w	varchar(255);
ds_mensagem_w			varchar(255);


BEGIN

select	max(nr_seq_faixa_salarial)
into STRICT	nr_seq_faixa_salarial_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

if (coalesce(nr_seq_faixa_salarial_w,0) <> coalesce(nr_seq_faixa_salarial_p,0)) then
	if (nr_seq_faixa_salarial_w IS NOT NULL AND nr_seq_faixa_salarial_w::text <> '') then
		select	ds_faixa_salarial
		into STRICT	ds_faixa_salarial_w
		from	pls_faixa_salarial
		where	nr_sequencia	= nr_seq_faixa_salarial_w;
	end if;
	if (nr_seq_faixa_salarial_p IS NOT NULL AND nr_seq_faixa_salarial_p::text <> '') then

		select	ds_faixa_salarial
		into STRICT	ds_faixa_salarial_nova_w
		from	pls_faixa_salarial
		where	nr_sequencia	= nr_seq_faixa_salarial_p;
	end if;

	ds_mensagem_w	:= 'Alterada a faixa salarial de ' || coalesce(ds_faixa_salarial_w,'*vazio*') || ' para ' || coalesce(ds_faixa_salarial_nova_w,'*vazio*') || '. ';

	update	pls_segurado
	set	nr_seq_faixa_salarial	= nr_seq_faixa_salarial_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_segurado_p;

	CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '85', clock_timestamp(), substr(ds_mensagem_w,1,255),
					'pls_alterar_faixa_salarial', null, null, null,
					null, null, null, null,
					null, null, null, null,
					nm_usuario_p, 'N');
commit;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
