CREATE OR REPLACE PROCEDURE pls_alterar_geracao_val_sca (nr_seq_vinculo_p bigint, ie_geracao_valores_p text, nm_usuario_p text, nr_seq_segurado_p bigint) AS $body$
DECLARE


nr_seq_vinculo_w		bigint;
ie_geracao_ant_w		varchar(10);
ds_historico_w			varchar(255);


BEGIN

select	coalesce(nr_sequencia, 0),
	ie_geracao_valores
into STRICT	nr_seq_vinculo_w,
	ie_geracao_ant_w
from	pls_sca_vinculo
where	nr_sequencia = nr_seq_vinculo_p;

if (nr_seq_vinculo_w <> 0) then

	select	'Tipo geração valores anterior: ' || CASE WHEN ie_geracao_ant_w='B' THEN 'Todos' WHEN ie_geracao_ant_w='T' THEN 'Titular' WHEN ie_geracao_ant_w='T' THEN 'D'  ELSE 'Dependentes' END  ||
		' para o tipo gerações valores : '|| CASE WHEN ie_geracao_valores_p='B' THEN 'Todos' WHEN ie_geracao_valores_p='T' THEN 'Titular' WHEN ie_geracao_valores_p='T' THEN 'D'  ELSE 'Dependentes' END
	into STRICT	ds_historico_w
	;

	update	pls_sca_vinculo
	set	ie_geracao_valores = ie_geracao_valores_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_vinculo_p;

	/* Gerar histórico */

	CALL pls_gerar_segurado_historico(	nr_seq_segurado_p, '43', clock_timestamp(), ds_historico_w,
					'pls_alterar_geracao_val_sca', null, null, null,
					null, null, null, null,
					null, null, nr_seq_vinculo_p, null,
					nm_usuario_p, 'N');
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
