SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_alterar_guia_prest_monit ( nr_seq_lote_com_p bigint, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	d.nr_seq_conta
	from	pls_monitor_tiss_glosa		a,
		pls_monitor_tiss_guia_ret	b,
		pls_monitor_tiss_lote_ret	c,
		pls_monitor_tiss_guia		d
	where	a.nr_seq_guia_monitor_ret	= b.nr_sequencia
	and	b.nr_seq_guia_monitor		= d.nr_sequencia
	and	b.nr_seq_lote_monitor_ret	= c.nr_sequencia
	and	c.nr_seq_lote_com		= nr_seq_lote_com_p
	and	a.cd_glosa			= '1308'
	and	coalesce(a.nr_seq_guia_proc_ret::text, '') = '';

BEGIN

for r_c01_w in C01 loop
	begin

	update	pls_conta
	set	cd_guia_prestador_ans	= to_char(r_c01_w.nr_seq_conta),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= r_c01_w.nr_seq_conta
	and	coalesce(cd_guia_prestador_ans::text, '') = '';

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_alterar_guia_prest_monit ( nr_seq_lote_com_p bigint, nm_usuario_p text) FROM PUBLIC;

